'use client';
import Header from '../../components/Header';
import Menu from '../../components/Menu';
import Footer from '../../components/Footer';
import { useCart } from '../../components/CartProvider';
import { useState } from 'react';
export default function CartPage(){
  const {items,remove,setQty,clear,total}=useCart();
  const [email,setEmail]=useState('');
  const [name,setName]=useState('');
  const [loading,setLoading]=useState(false);
  async function checkout(){
    if(!items.length) return alert('Your cart is empty');
    if(!email||!name) return alert('Please enter name and email');
    setLoading(true);
    try{
      const origin = window.location.origin;
      const res = await fetch('/api/payfast/identifier',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({
        items, buyer:{email,name}, return_url: origin+'/thank-you', cancel_url: origin+'/cancelled'
      })});
      const data = await res.json().catch(()=>({}));
      if(res.ok && data && data.uuid){
        if(typeof window!== 'undefined' && window.payfast_do_onsite_payment){
          window.payfast_do_onsite_payment({ uuid: data.uuid, return_url: data.return_url, cancel_url: data.cancel_url });
        } else {
          alert('Payfast engine not loaded. Please retry.');
        }
      } else {
        alert(data.error || 'Unable to start checkout');
      }
    } finally { setLoading(false); }
  }
  return (<div className='container'><Header/><Menu/>
  <section className='hero hero--slim'><div className='hero__content'><h1 className='hero__title'>Your Cart</h1><p className='hero__subtitle'>Review items and proceed to secure checkout.</p></div></section>
  <section className='section'>
    {items.length? (<div style={{display:'grid',gap:'0.75rem'}}>{items.map(i=> (<div key={i.id} className='card' style={{display:'grid',gridTemplateColumns:'1fr auto',alignItems:'center'}}><div><div className='h3'>{i.name}</div><div className='muted'>R {i.price.toFixed(2)}</div></div><div style={{display:'flex',gap:'.5rem',alignItems:'center'}}><input type='number' min='1' value={i.qty} onChange={e=>setQty(i.id,parseInt(e.target.value||'1'))} style={{width:'64px'}}/><button className='btn' onClick={()=>remove(i.id)}>Remove</button></div></div>))}</div>):(<p className='muted'>Your cart is empty.</p>)}
    <hr style={{border:'none',borderTop:'1px solid var(--border)',margin:'1rem 0'}}/>
    <div style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}><strong>Total</strong><strong>R {total.toFixed(2)}</strong></div>
    <div className='form' style={{marginTop:'1rem'}}><div className='form__row'><label>Name<input value={name} onChange={e=>setName(e.target.value)} placeholder='First Last' required/></label><label>Email<input type='email' value={email} onChange={e=>setEmail(e.target.value)} required/></label></div></div>
    <div className='cta'><button className='btn btn--primary' disabled={loading||!items.length} onClick={checkout}>{loading?'Starting‚Ä¶':'Pay with Payfast'}</button><button className='btn btn--ghost' onClick={clear} disabled={!items.length}>Clear</button></div>
  </section>
  <Footer/></div>);
}

