files.app/api/payfast/identifier/route.js:
import { NextResponse } from 'next/server';
import { buildSignatureHeaders, sumItems, normaliseBuyer, API_BASE, ONSITE_PATH } from '../../../../lib/payfast';
export async function POST(req){
  try{
    const body = await req.json();
    const { items=[], buyer={}, return_url, cancel_url } = body||{};
    if(!items.length) return NextResponse.json({error:'No items'}, {status:400});
    const amount = sumItems(items);
    const buyerNorm = normaliseBuyer(buyer);
    const merchant_id = process.env.PAYFAST_MERCHANT_ID;
    const passphrase = process.env.PAYFAST_PASS_PHRASE;
    if(!merchant_id || !passphrase){
      return NextResponse.json({error:'Payfast not configured (missing env vars)'},{status:400});
    }
    const m_payment_id = `ord_${Date.now()}`;
    const data = {
      amount: amount.toFixed(2),
      item_name: `Order ${m_payment_id}`,
      m_payment_id,
      email_address: buyerNorm.email,
      name_first: buyerNorm.firstName,
      name_last: buyerNorm.lastName,
      return_url,
      cancel_url,
      notify_url: `${req.nextUrl.origin}/api/payfast/itn`
    };
    const headers = buildSignatureHeaders(data);
    const testing = process.env.PAYFAST_TESTING ? '?testing=true' : '';
    const url = `${API_BASE()}${ONSITE_PATH}${testing}`;
    const res = await fetch(url, { method:'POST', headers, body: JSON.stringify(data) });
    const json = await res.json().catch(()=>null);
    if(!res.ok || !json){
      return NextResponse.json({error: json?.message || 'Payfast identifier error'}, {status: 500});
    }
    // Expecting {uuid: '...'} per Payfast Onsite flow
    return NextResponse.json({ uuid: json.uuid || json.identifier || json.data?.uuid, return_url, cancel_url });
  }catch(err){
    return NextResponse.json({error: 'Server error creating identifier'}, {status:500});
  }
}

