import { NextResponse } from 'next/server';
import { verifyItn } from '../../../../lib/payfast';
export async function POST(req){
  try{
    const raw = await req.text(); // ITN posts as form-encoded
    const url = new URL(req.url);
    const originIP = req.headers.get('x-forwarded-for') || req.headers.get('x-real-ip') || 'unknown';
    const { ok, reason, data } = await verifyItn(raw, originIP);
    if(!ok){
      console.warn('ITN verification failed', { reason, originIP });
      return new NextResponse('Bad Request', { status: 400 });
    }
    // TODO: update your order in DB using data.m_payment_id / data.pf_payment_id / data.payment_status
    return new NextResponse('OK');
  }catch(e){
    console.error('ITN error', e);
    return new NextResponse('Server Error', { status: 500 });
  }
}

