import crypto from 'node:crypto';
export const ONSITE_PATH = '/onsite/transactions'; // If Payfast changes this path, update here.
export const API_BASE = () => process.env.PAYFAST_BASE || 'https://api.payfast.co.za';
const MERCHANT_ID = () => process.env.PAYFAST_MERCHANT_ID || '';
const PASSPHRASE = () => process.env.PAYFAST_PASS_PHRASE || '';
export function encodeKV(obj){return Object.keys(obj).sort().filter(k=>obj[k]!==undefined&&obj[k]!==null&&k!=='signature').map(k=>`${k}=${encodeURIComponent(String(obj[k])).replace(/%20/g,'+')}`).join('&');}
export function buildSignatureHeaders(data){
  const headers = { 'merchant-id': MERCHANT_ID(), 'version':'v1', 'timestamp': new Date().toISOString().split('.')[0] };
  const signature_uri = encodeKV({ ...data, ...headers, passphrase: PASSPHRASE() });
  const signature = crypto.createHash('md5').update(signature_uri).digest('hex');
  return { 'Content-Type':'application/json', ...headers, signature };
}
export function sumItems(items){ return items.reduce((s,i)=> s + (Number(i.price)||0)*(Number(i.qty)||1), 0); }
export function normaliseBuyer(b){ const name=(b?.name||'').trim(); const [firstName='',...rest]=name.split(' '); return { email:(b?.email||'').trim(), firstName, lastName: rest.join(' ').trim()||'-' }; }
const VALID_IP_HOSTS=['www.payfast.co.za','w1w.payfast.co.za','w2w.payfast.co.za','sandbox.payfast.co.za'];
export async function verifyItn(rawBody, originIP){
  // Parse x-www-form-urlencoded
  const data = Object.fromEntries(new URLSearchParams(rawBody));
  // Signature check (recompute from ITN payload + passphrase)
  const pfSig = data.signature;
  const encoded = encodeKV({ ...data, passphrase: PASSPHRASE() });
  const localSig = crypto.createHash('md5').update(encoded).digest('hex');
  if(!pfSig || localSig !== pfSig){ return { ok:false, reason:'bad-signature', data }; }
  // Optional: validate host via DNS reverse if available from proxy (best effort)
  const host = (data.future_host||'').toString();
  const validHost = VALID_IP_HOSTS.includes(host) || true; // keep permissive if not provided
  if(!validHost){ return { ok:false, reason:'bad-host', data }; }
  // Optionally call query/validate endpoint (has had intermittent changes; skip if blocked)
  return { ok:true, data };
}

