files.components/CartProvider.js:
'use client';
import { createContext, useContext, useMemo, useState } from 'react';
const CartContext = createContext(null);
export function useCart(){ return useContext(CartContext); }
export default function CartProvider({children}){
  const [items,setItems]=useState([]);
  const add=(product,qty=1)=> setItems(prev=>{ const i=prev.findIndex(p=>p.id===product.id); if(i>-1){ const c=[...prev]; c[i]={...c[i], qty:c[i].qty+qty}; return c;} return [...prev,{...product,qty}]});
  const remove=id=> setItems(prev=>prev.filter(p=>p.id!==id));
  const setQty=(id,qty)=> setItems(prev=>prev.map(p=>p.id===id?{...p,qty:Math.max(1,Number(qty)||1)}:p));
  const clear=()=> setItems([]);
  const total=useMemo(()=> items.reduce((s,p)=> s+p.price*p.qty,0),[items]);
  const value={items,add,remove,setQty,clear,total};
  return <CartContext.Provider value={value}>{children}</CartContext.Provider>;
}

