.hero--slim{padding:2.25rem 1.25rem}
.hero__content{max-width:800px;margin:0 auto;text-align:center}
.badge{display:inline-flex;align-items:center;padding:.4rem .65rem;border-radius:999px;background:rgba(34,211,238,0.15);color:#cffafe;font-weight:600;letter-spacing:.2px;margin-bottom:.75rem}
.hero__title{font-size:2.2rem;margin-bottom:.6rem}
.hero__subtitle{color:var(--muted);font-size:1.1rem}
.hero__actions{display:flex;gap:.75rem;justify-content:center;margin-top:1rem;flex-wrap:wrap}
.btn{display:inline-flex;align-items:center;justify-content:center;padding:.7rem 1rem;border-radius:12px;border:1px solid var(--border);background:#0b1220;color:var(--text);cursor:pointer;transition:transform .15s ease,background .2s ease,border-color .2s;white-space:nowrap}
.btn:active{transform:translateY(1px)}
.btn--primary{background:linear-gradient(90deg,var(--brand),var(--brand-2));color:#02141a;border-color:transparent;font-weight:700}
.btn--ghost{background:transparent}
.footer{display:flex;flex-direction:column;gap:.3rem;align-items:center;justify-content:center;margin:2rem 0 0;color:var(--muted)}
.features{display:grid;gap:.5rem;margin:0;padding-left:1rem}
.prose{color:var(--text);opacity:.95}
.cta{display:flex;gap:.75rem;flex-wrap:wrap;margin-top:1rem}
.form{display:flex;flex-direction:column;gap:.75rem}
.form__row{display:grid;grid-template-columns:1fr 1fr;gap:.75rem}
@media (max-width:640px){.form__row{grid-template-columns:1fr}}
label{display:flex;flex-direction:column;gap:.35rem;font-weight:600}
input,textarea{background:#0a1326;border:1px solid var(--border);border-radius:10px;padding:.65rem .8rem;color:var(--text)}
input:focus,textarea:focus{outline:none;border-color:var(--brand)}
.checklist{display:grid;gap:.5rem;margin:0;padding-left:1.1rem}
.checklist li{margin-left:.2rem}

/* --- New header + hamburger styles --- */
.site-header{margin:1rem 0 1.25rem}
.site-header__inner{display:flex;align-items:center;gap:1rem}
.site-header__brand{display:flex;align-items:center;gap:.6rem;font-weight:800;letter-spacing:.2px}
.site-header__nav{margin-left:auto;margin-right:auto}
.site-header__actions{display:flex;align-items:center;gap:.5rem;margin-left:auto}
.icon-btn{display:inline-flex;align-items:center;justify-content:center;width:38px;height:38px;border:1px solid var(--border);border-radius:12px}
.icon-btn:hover{border-color:var(--brand)}
.nav__list{display:flex;gap:1.25rem;list-style:none;padding:0;margin:0}
.nav__link{padding:.5rem .75rem;border-radius:10px;position:relative}
.nav__link::after{content:'';position:absolute;left:10px;right:10px;bottom:6px;height:2px;background:linear-gradient(90deg,var(--brand),var(--brand-2));transform:scaleX(0);transform-origin:left;transition:transform .25s ease}
.nav__link:hover::after,.nav__link:focus::after{transform:scaleX(1)}

/* Hamburger */
.nav__toggle{display:none;align-items:center;justify-content:center;width:44px;height:38px;border:1px solid var(--border);border-radius:12px;background:transparent;cursor:pointer;transition:background .2s ease,border-color .2s ease;z-index:60}
.nav__toggle:hover{border-color:var(--brand)}
.nav__toggle-box{position:relative;width:20px;height:14px}
.nav__toggle-bar,.nav__toggle-box::before,.nav__toggle-box::after{content:'';position:absolute;left:0;right:0;height:2px;background:linear-gradient(90deg,var(--brand),var(--brand-2));transition:transform .2s ease,opacity .2s ease,top .2s ease}
.nav__toggle-bar{top:6px}
.nav__toggle-box::before{top:0}
.nav__toggle-box::after{top:12px}
.nav__toggle.is-open .nav__toggle-bar{opacity:0}
.nav__toggle.is-open .nav__toggle-box::before{transform:translateY(6px) rotate(45deg)}
.nav__toggle.is-open .nav__toggle-box::after{transform:translateY(-6px) rotate(-45deg)}

.nav__sheet{position:fixed;top:0;right:-100%;width:72%;max-width:320px;height:100vh;background:var(--panel);border-left:1px solid var(--border);padding:1rem;transition:right .25s ease;z-index:55;display:flex;align-items:flex-start}
.nav__sheet.is-open{right:0}
.nav__backdrop{position:fixed;inset:0;background:rgba(2,8,23,.55);backdrop-filter:blur(2px);z-index:50}
.nav__list--mobile{flex-direction:column;gap:.75rem}
.nav__list--mobile .nav__link{display:block;padding:.75rem 1rem;border:1px solid var(--border);border-radius:12px}
.nav__list--desktop{display:flex}

/* Responsive */
@media (max-width:900px){
  .nav__list--desktop{display:none}
  .nav__toggle{display:inline-flex}
}
